actor Counter {
    private var value = 0

    func increment() {
        value += 1
    }

    func getValue() -> Int {
        return value
    }
}

Task {
    let counter = Counter()
    await counter.increment()
    let value = await counter.getValue()
    print(value)
}

struct AnimatedView: View {
    @State private var scale: CGFloat = 1.0

    var body: some View {
        Circle()
            .frame(width: 100, height: 100)
            .scaleEffect(scale)
            .onTapGesture {
                withAnimation(.easeInOut(duration: 0.5)) {
                    scale = scale == 1.0 ? 1.5 : 1.0
                }
            }
    }
}

